package com.syntax.class01;

public class Homework1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		byte box1 = 127; // 8 bit -128 +127
		short box2=32767; // 16 bit -32768 +32767
		int box3=2147483647; // 32 bit -2147483648 +2147483637
		long box4=4096; // 64 bit -9223372836854775808 +9223372836854775807
		
		float varible1=3.99F; // 32 bit 1.1234567
		double varible2=100.0; // 64 bit 1.1-15
		
		char cantainer='@'; // 16 bit
		char dollar='$';
		
		boolean hot=true; // 1 bit
		boolean tired=false;
		
		System.out.println(varible2);
		System.out.println("verible2");
		
		System.out.println(box1);
		System.out.println(box2);	
		System.out.println(box3);	
		System.out.println(box4);	
		System.out.println(varible1);
		System.out.println(varible2);
		System.out.println(cantainer);	
		System.out.println(dollar);	
	    System.out.println(hot);  
		System.out.println(tired);
	}

}
