package com.syntax.class02;

public class NonPrimitiveDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 
		//String is just 1 example of non primitive data type
		
		String name="Asma";
		
		String address="123 address street";
		
		String phone="1234567890";
		
		String age="17";
		
		String character="A";
		
		//ctr+spase -> autocompletes the statement
		//syso +ctlr =space
		
		System.out.println(20); 
		
		// "My name is" Asma
		
		System.out.println("My name is "+ name); 
		
		//Asma is 17
		System.out.println(name + " is "+  age);
		
		//The price of the apple is 1.99
		
		String fruit ="apple";
		double price=1.99;
		
		System.out.println("The price of the "+fruit+ " is "+ price); 
		
		fruit="mango";
		price=5.99;
		
		System.out.println("The price of the "+fruit+ " is "+ price);
		
		/*to attach any value (int, double, boolean, char, String etc)
		 * to a String we use +
		 * + nex to the String acts as Concatenation
		 */
		  
	}

}
