package com.syntax.class04;

public class PositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     
		int number=2;
		if(number>0)
		{
			System.out.println("Number is positive");
		}
		else
		{
			System.out.println("Number is Negative");
	
		}
	
	}
}